import React from "react";
import {Card,CardBody} from "reactstrap";
import {Link} from "react-router-dom";

const TourCard = ({tour}) => {
    const {id,title,city,photo,price,featured,avgRating,reviews}=tour;
    return <div className="tour__card">
        <Card>
            <div className="tour__img">
                <img src={photo} alt=""/>
                <span>Featured</span>
            </div>
        </Card>
        <CardBody>
            <div className="card__top d-felx align-items-center justify-content-between">
                <span className="tour__location d-flex align-items-center gap-1">
                    <i class="ri-map-pin-line"></i> {city}
                </span>
                <span className="tour__location d-flex align-items-center gap-1">
                    <i class="ri-star-line"></i> {avgRating} {" "}
                    <span>({reviews.lenght})</span>
                </span>
            </div>
            <h5 className="tour__title">
                <Link to={`/tours/${id}`}>{title}
                </Link>
            </h5>
            <div className="card__bottom d-flex align-items-center justify-content-between mt-3">
                <h5>{price} <span> /per person </span>
                </h5>
                <button className="btn booking__btn">
                    <Link to={`/tours/${id}`}>Book Now</Link>
                </button>
            </div>
        </CardBody>
    </div>;
};

export default TourCard;
